pragma solidity ^0.4.0;

import "./Amendable.sol";

/**
 * Ownership documentation for a listing
 */
contract Assignment is Amendable {

    string private firstName;
    string private lastName;
    string private companyName;

    function Assignment(string _firstName,
                        string _lastName,
                        string _companyName) {
        firstName = _firstName;
        lastName = _lastName;
        companyName = _companyName;
    }

    // Restricted getters
    //-------------------

    function getFirstName() external constant restricted returns (string) {
        return firstName;
    }
    function getLastName() external constant restricted returns (string) {
        return lastName;
    }
    function getCompanyName() external constant restricted returns (string) {
        return companyName;
    }
}
