pragma solidity ^0.4.0;

import "./owned.sol";

/**
 * Eternal Storage contract to keep track of Cognate Listings
 */
contract CognateRegistry is owned {

    // mapping to keep track of all Cognate Listings
    mapping(string => address) listingMap;
    address[] listingArray;

    // Events
    event Added(string key, address listing);

    //--------------
    // External API
    //--------------

    // Restricted
    //-----------

    /**
     * TODO
     */
    function add(string key, address listing) external restricted {
        listingMap[key] = listing;
        listingArray.push(listing);
        Added(key, listing);
    }

    // Public
    //-------

    /**
     * Verify if a supplied address is that of a Cognate Listing
     */
    function verify(address listing) external constant restricted returns (bool) {
        for(uint i = 0; i < listingArray.length; i++) {
            if(listingArray[i] == listing) {
                return true;
            }
        }

        return false;
    }

    /**
     * Retrieve the address of a listing with the given mark
     */
    function getListing(string key) external constant restricted returns (address) {
        return listingMap[key];
    }
}
